import React, { Component } from "react";
import "./Signup.css";
import {withProvider} from "../GlobalProvider.js"

class Signup extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            username: "", 
            password: "", 


        }
    }

    validateForm() {
        return this.state.password.length > 0 && this.state.username.length > 0;
    }

    handleChange = event => {
        this.setState({
          [event.target.id]: event.target.value
        });
    }

    handleSubmit = event => {
        event.preventDefault();
        console.log(this.props);
        this.props.signup(this.state)
            .then(() => this.props.history.push("/chatpage"));

    }

    render() { 
        return ( 
            <div className="container">
                <div className="text"> Sign Up here </div>
            <form onSubmit={this.handleSubmit}>
                <div class="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                        <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
                    </div>
                    <div class="form-group">
                        <label for="exampleInputPassword1">Password</label>
                        <input type="password" class="form-control" id="exampleInputPassword1" placeholder="Password"/>
                    </div>
                    <button type="submit" class="btn btn-primary">Submit</button>
            </form>
           </div>
        );
    }
}

export default withProvider(Signup);
